import React from 'react'
import HomePage from '../pages/HomePage'

const Map = () => {
  let height = 10
  let width = 10
  let foodTruckOne = [
      {x: 7, y: 6}
  ]

  let foodtruck = (y, x) => {
    let result = false
    foodTruckOne.forEach((element) => {
      if (element.x === x && element.y === y) {
        result = true
      }
    })
    return result
  } 

  const mapContainer = document.querySelector('.mapContainer')

  const mapGrid = () => {
    for (let i = 0; i < height; i++) {
      for (let j = 0; j < width; j++) {
        let gridPosition = document.createElement('div')
        if (foodtruck(i,j)) {
          gridPosition.setAttribute('className', `x${i}y${j} foodtruckOne`)
        }
        else {
          gridPosition.setAttribute('className', `x${i}y${j}`)
        }
        mapContainer.appendChild(gridPosition)
      }
    }
  }

  return(
    mapGrid()
  )
}

export default Map
